import { ServiceService } from './../../service/service.service';
import { Component } from '@angular/core';

@Component({
  selector: 'app-posts',
  templateUrl: './posts.component.html',
  styleUrls: ['./posts.component.css']
})
export class PostsComponent {

  token: string | null = ""
  loggedemail:string | null=""

  constructor(private configService: ServiceService) { }



  ngOnInit(): void {
    //get the token
    this.token = localStorage.getItem('token');
    this.loggedemail=localStorage.getItem('email');

    //retrive the offers (default category electronics)
    if (this.token != null) {
      this.configService.getOffers(this.token, "Electronics").subscribe((data: Offer[]) => {
        console.log(data);
        this.offers = data;
      },
        error => {
         if(this.offers.length==0){
          this.pageError = "No offer available !! try posting some offer"
          this.showError = true
          setTimeout(function() {
            $('#errordiv').fadeOut('fast');
        }, 1000);
        $("#errordiv").show();
         }else{
          console.log(error);
          this.pageError = "We encountered some error please try again later"
          this.showError = true
          setTimeout(function() {
            $('.errordiv').fadeOut('fast');
        }, 1000);
        $("#errordiv").show();
         }
        });
    }


  }



}
