import { Component } from '@angular/core';
import { FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthResponse } from 'src/app/model/authResponse';
import { User } from 'src/app/model/user';
import { ServiceService } from 'src/app/service/service.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  user:User=new User();
  userForm: FormGroup = new FormGroup({})
  formError = ""


  constructor(private service:ServiceService,private router:Router){}



  ngOnInit(): void {

    if(localStorage.getItem("token")){
      this.router.navigate(['/main'])
    }
  }




  LoginUser(){
    this.service.loginUser(this.user).subscribe({
      next: (res) =>{




        const token=JSON.stringify(res);
        //const dataTobeSave=JSON.parse(token);

        console.log(token);
        // localStorage.setItem("email",res.email)
        //  localStorage.setItem("token",token)

        // localStorage.setItem("token",'sass')
   //localStorage.setItem("email",res['email'])
     localStorage.setItem("token",token);


      //  localStorage.setItem("email",res.email)
        this.router.navigate(['/','get'])
      },
      error: (error) => console.error(error),
      complete: () => console.info('User Registered') 

  })


  }

  onSubmit(){
    console.log(this.user)
    this.LoginUser();
  }


}
<div class="container text-center">
    <form class="form-signin" (ngSubmit)="onSubmit()" [formGroup]="userForm">
      <img class="mb-4" src="https://getbootstrap.com/docs/4.0/assets/brand/bootstrap-solid.svg" alt="" width="72" height="72">
      <h1 class="h3 mb-3 font-weight-normal">Login into Your Account</h1>


      <label for="inputEmail" class="sr-only">Email address</label>
      <input type="email"  class="form-control" [(ngModel)]="user.email" name="user.email" placeholder="Email address" required="" autofocus="">


      <label for="inputPassword" class="sr-only">Password</label>
      <input type="password"  class="form-control" [(ngModel)]="user.password" name="user.password" placeholder="Password" required="">
      <div class="checkbox mb-3">
        <label>
          <input type="checkbox" value="remember-me"> Remember me
        </label>
      </div>
      <button class="btn btn-lg btn-primary btn-block" type="submit">Sign in</button>
      <p class="mt-5 mb-3 text-muted">© 2017-2018</p>
    </form>
  </div>
